  const [isDisabled, setIsDisabled] = useState(false);

  const handleAsyncAction = async () => {
    setIsLoading(true);

    try {
      if (onAsyncAction) {
        await onAsyncAction();
      } else {
        await new Promise(resolve => setTimeout(resolve, asyncDelay));
      }
    } finally {
      setIsLoading(false);
    }
  };

